  function mycallback(json) {
    for (var i = 0; i < json.feed.entry.length; i++) {
      for (var j = 0; j < json.feed.entry[i].link.length; j++) {
        if (json.feed.entry[i].link[j].rel == 'alternate') {
          var postUrl = json.feed.entry[i].link[j].href;
          break;
        }
      }
	  var currentDiv = json.feed.updated.$t
	  var postthumb = json.feed.entry[i].media$thumbnail.url
      var postTitle = json.feed.entry[i].title.$t;
      var postAuthor = json.feed.entry[i].author[0].name.$t;
      var postSummary = json.feed.entry[i].summary.$t;
      var entryShort = postSummary.substring(0, 90);
      var entryEnd = entryShort.lastIndexOf(" ");
      var postContent = entryShort.substring(0, entryEnd) + '...';
      var item = '<div class="item"><a href=' + postUrl + '><img src=" '+ postthumb.replace(/\/s72\-c/,"/s"+1600+"-a") +'"><h3>'+ postTitle +'</h3></a></div>';
      document.write(item);
    }
  }
